# Split the markdown data by lines
lines = markdown_data.splitlines()

# Output CSV file path
csv_output_path = './static/data/medical_image_datasets.csv'

# Process the markdown to extract dataset names, descriptions, and section headers
header = ["Section", "Dataset", "Description"]
section = None
rows = []

for line in lines:
    # Identify section headers
    if line.startswith("### "):
        section = line.replace("### ", "")
    # Identify dataset rows
    elif line.startswith("| ["):
        parts = line.split("|")
        dataset = parts[1].split("]")[0].replace("[", "").split("/")[-1].replace(".md", "")
        description = parts[2].strip()
        rows.append([section, dataset, description])

# Write to CSV
with open(csv_output_path, mode='w', newline='') as csv_file:
    writer = csv.writer(csv_file)
    writer.writerow(header)
    writer.writerows(rows)

print("CSV file has been created successfully:", csv_output_path)
